$date = '2023-03-30';
$c = new DateTime($date);
$m = $c->format('m');
$d = $c->format('d');
$step = new DateInterval('P1D');

if ($d < 29)
{
    $c->setDate($c->format('Y'), $c->format('m'), 28);
}

while($m == $c->format('m'))
{
    $c->add($step);
}

echo $c->format('Y-m-d') .PHP_EOL;
